/**
 * @module ScramjetCore
 * @file buffer-stream.js
 */

const scramjet = require('.');

/**
 * A factilitation stream created for easy splitting or parsing buffers.
 *
 * Useful for working on built-in Node.js streams from files, parsing binary formats etc.
 *
 * A simple use case would be:
 *
 * ```javascript
 *  fs.createReadStream('pixels.rgba')
 *      .pipe(new BufferStream)         // pipe a buffer stream into scramjet
 *      .breakup(4)                     // split into 4 byte fragments
 *      .parse(buf => [
 *          buf.readInt8(0),            // the output is a stream of R,G,B and Alpha
 *          buf.readInt8(1),            // values from 0-255 in an array.
 *          buf.readInt8(2),
 *          buf.readInt8(3)
 *      ]);
 * ```
 *
 * @extends DataStream
 */
class BufferStream extends scramjet.DataStream {

    /**
     * Creates the BufferStream
     *
     * @param {object} opts Stream options passed to superclass
     * @example {@link ../samples/buffer-stream-constructor.js}
     */
    constructor(...args) {
        super(...args);
        this.buffer = [];
    }

    /**
     * Pop callback
     *
     * @callback ShiftCallback
     * @param {Buffer} shifted shifted bytes
     */

    /**
     * Shift given number of bytes from the original stream
     *
     * Works the same way as {@see DataStream.shift}, but in this case extracts
     * the given number of bytes.
     *
     * @param {Number} chars The number of bytes to shift
     * @param {ShiftCallback} func Function that receives a string of shifted bytes
     * @return {BufferStream}  substream
     *
     * @example {@link ../samples/string-stream-shift.js}
     */
    shift(bytes, func) {
        const ret = Buffer.alloc(bytes);
        const str = this.tap()._selfInstance();
        let offs = 0;

        const chunkHandler = (chunk) => {
            const length = Math.min(ret.length - offs, chunk.length);
            chunk.copy(ret, offs, 0, length);
            offs += length;
            if (length >= bytes) {
                unHook()
                    .then(
                        () => {
                            str.write(chunk.slice(length));
                            this.pipe(str);
                        }
                    );
            }
        };

        const endHandler = (...args) => {
            if (ret.length < bytes) {
                unHook();
            }
            str.end(...args);
        };

        const errorHandler = str.emit.bind(str, "error");

        const unHook = (async () => {
            this.removeListener("data", chunkHandler);
            this.removeListener("end", endHandler);
            this.removeListener("error", errorHandler);
            return func(ret);
        });


        this.on("data", chunkHandler);
        this.on("end", endHandler);
        this.on("error", errorHandler);

        return str;
    }

    /**
     * Splits the buffer stream into buffer objects
     *
     * @todo implement splitting by function
     * @todo implement asynchronous splitter
     * @param  {String|Buffer} splitter the buffer or string that the stream
     *                                  should be split by.
     * @return {BufferStream}  the re-splitted buffer stream.
     * @example {@link ../samples/buffer-stream-split.js}
     */
    split(splitter) {
        if (splitter instanceof Buffer || typeof splitter === "string") {
            const needle = Buffer.from(splitter);
            return this.tap().pipe(this._selfInstance({
                transform(buffer, enc, callback) {
                    if (Buffer.isBuffer(this._haystack) && this._haystack.length > 0) {
                        this._haystack = Buffer.from([this._haystack, buffer]);
                    } else {
                        this._haystack = buffer;
                    }

                    let pos;
                    while((pos = this._haystack.indexOf(needle)) > -1) {
                        this.push(Buffer.from(this._haystack.slice(0, pos)));
                        this._haystack = this._haystack.slice(pos + needle.length);
                    }

                    callback();
                },
                flush(callback) {
                    if (this._haystack.length) this.push(this._haystack);
                    this._haystack = null;
                    callback();
                }
            }));
        }
    }

    /**
     * Breaks up a stream apart into chunks of the specified length
     *
     * @param  {Number} number the desired chunk length
     * @return {BufferStream}  the resulting buffer stream.
     * @example {@link ../samples/buffer-stream-breakup.js}
     */
    breakup(number) {
        if (number <= 0 || !isFinite(+number))
            throw new Error("Breakup number is invalid - must be a positive, finite integer.");

        return this.tap().pipe(this._selfInstance({
            transform(chunk, encoding, callback) {
                if (Buffer.isBuffer(this.buffer)) {
                    chunk = Buffer.concat([this.buffer, chunk]);
                }
                let offset;
                for (offset = 0; offset < chunk.length - number; offset += number) {
                    this.push(chunk.slice(offset, offset + number));
                }
                this.buffer = chunk.slice(offset);
                callback();
            },
            flush(callback) {
                this.push(this.buffer);
                this.buffer = null;
                callback();
            }
        }));

    }

    /**
     * Creates a string stream from the given buffer stream
     *
     * Still it returns a DataStream derivative and isn't the typical node.js
     * stream so you can do all your transforms when you like.
     *
     * @param  {String} encoding The encoding to be used to convert the buffers
     *                           to streams.
     * @return {StringStream}  The converted stream.
     * @example {@link ../samples/buffer-stream-tostringstream.js}
     */
    stringify(encoding) {
        return this.pipe(new scramjet.StringStream(encoding || 'utf-8', {objectMode: true}));
    }

    /**
     * Alias for {@link BufferStream#stringify}
     * @function toStringStream
     */

    /**
     * @callback ParseCallback
     * @param {Buffer} chunk the transformed chunk
     * @return {Promise}  the promise should be resolved with the parsed object
     */

    /**
     * Parses every buffer to object
     *
     * The method MUST parse EVERY buffer into a single object, so the buffer
     * stream here should already be splitted or broken up.
     *
     * @param  {ParseCallback} parser The transform function
     * @return {DataStream}  The parsed objects stream.
     * @example {@link ../samples/buffer-stream-parse.js}
     */
    parse(parser) {
        return this.tap().map(parser, scramjet.DataStream);
    }

    /**
     * Alias for {@link BufferStream#parse}
     * @function toDataStream
     */

    /**
     * @ignore
     */
    _transform(chunk, encoding, callback) {
        this.push(Buffer.from(chunk, encoding));
        return callback();
    }

}

BufferStream.prototype.pop = BufferStream.prototype.shift;
BufferStream.prototype.toDataStream = BufferStream.prototype.parse;
BufferStream.prototype.toStringStream = BufferStream.prototype.stringify;

module.exports = BufferStream;

/**
 * @module ScramjetCore
 */

const {PromiseTransformStream} = require('./util/promise-transform-stream');
const {Readable} = require("stream");
const scramjet = require('./');

// const cpus = require("os").cpus();

/**
 * Standard options for scramjet streams.
 *
 * @typedef {Object} StreamOptions
 * @property {Number} maxParallel the number of transforms done in parallel
 * @property {DataStream} referrer a referring stream to point to (if possible the transforms will be pushed to it
 *                                 instead of creating a new stream)
 */

/**
 * DataStream is the primary stream type for Scramjet. When you parse your
 * stream, just pipe it you can then perform calculations on the data objects
 * streamed through your flow.
 *
 * @classdesc
 * @extends stream.PassThrough
 */
class DataStream extends PromiseTransformStream {

    /**
     * Create the DataStream.
     *
     * @param {StreamOptions} opts Stream options passed to superclass
     *
     * @example {@link ../samples/data-stream-constructor.js}
     */
    constructor(opts) {
        super(Object.assign({
            objectMode: true,
            writableObjectMode: true,
            readableObjectMode: true
        }, opts));
        this._error_handlers = [];
    }

    /**
     * @callback MapCallback
     * @param {*} chunk the chunk to be mapped
     * @returns {Promise|*}  the mapped object
     */

    /**
     * Transforms stream objects into new ones, just like Array.prototype.map
     * does.
     *
     * @param {MapCallback} func The function that creates the new object
     * @param {Class} Clazz (optional) The class to be mapped to.
     * @return {DataStream}  mapped stream
     *
     * @example {@link ../samples/data-stream-map.js}
     */
    map(func, Clazz) {
        Clazz = Clazz || this.constructor;
        return this.pipe(new Clazz({
            parallelTransform: func,
            referrer: this
        }));
    }

    /**
     * @callback FilterCallback
     * @param {*} chunk the chunk to be filtered or not
     * @returns {Promise|Boolean}  information if the object should remain in
     *                             the filtered stream.
     */

    /**
     * Filters object based on the function outcome, just like
     * Array.prototype.filter.
     *
     * @param  {FilterCallback} func The function that filters the object
     * @return {DataStream}  filtered stream
     *
     * @example {@link ../samples/data-stream-filter.js}
     */
    filter(func) {
        return this.pipe(this._selfInstance({
            parallelTransform: func,
            afterTransform: (chunk, ret) => ret ? chunk : Promise.reject(DataStream.filter),
            referrer: this
        }));
    }

    /**
     * @callback ReduceCallback
     * @param {*} acc the accumulator - the object initially passed or retuned
     *                by the previous reduce operation
     * @param {Object} chunk the stream chunk.
     * @return {Promise|*}  accumulator for the next pass
     */

    /**
     * Reduces the stream into a given accumulator
     *
     * Works similarily to Array.prototype.reduce, so whatever you return in the
     * former operation will be the first operand to the latter.
     *
     * This method is serial - meaning that any processing on an entry will
     * occur only after the previous entry is fully processed. This does mean
     * it's much slower than parallel functions.
     *
     * @param  {TransformFunction} func The into object will be passed as the  first argument, the data object from the stream as the second.
     * @param  {Object} into Any object passed initally to the transform function
     * @return {Promise}  Promise resolved by the last object returned by the call of the transform function
     *
     * @example {@link ../samples/data-stream-reduce.js}
     */
    reduce(func, into) {

        return new Promise((res, rej) => {

            let last = Promise.resolve(into);

            this.tap().pipe(new PromiseTransformStream({
                parallelTransform: (chunk) => {
                    return last = last.then((acc) => func(acc, chunk));
                },
                referrer: this,
                initial: into
            }))
            .on("end", () => last.then(res))
            .on("error", rej)
            .resume();

        });

    }

    /**
     * Calls the passed method in place with the stream as first argument, returns result.
     *
     * The main intention of this method is to run scramjet modules - transforms that allow complex transforms of
     * streams. These modules can also be run with [scramjet-cli](https://github.com/signicode/scramjet-cli) directly
     * from the command line.
     *
     * @param  {Function|String} func if passed, the function will be called on self
     *                         to add an option to inspect the stream in place,
     *                         while not breaking the transform chain.
     *                         Alternatively this can be a relative path to a scramjet-module.
     * @return {*}  anything the passed function returns
     *
     * @example {@link ../samples/data-stream-use.js}
     */
    use(func) {
        switch (typeof func) {
            case "function":
                return func(this);
            case "string":
                return require(func)(this);
            default:
                throw new Error();
        }
    }

    /**
     * @callback TeeCallback
     * @param {DataStream} teed The teed stream
     */

    /**
     * Duplicate the stream
     *
     * Creates a duplicate stream instance and pases it to the callback.
     *
     * @param {TeeCallback} func The duplicate stream will be passed as first argument.
     * @return {DataStream}  self
     *
     * @example {@link ../samples/data-stream-tee.js}
     */
    tee(func) {
        func(this.pipe(this._selfInstance()));
        return this.tap();
    }

    /**
     * Performs an operation on every chunk, without changing the stream
     *
     * This is a shorthand for ```stream.on("data", func)``` but with flow control.
     * Warning: this resumes the stream!
     *
     * @chainable
     * @param  {MapCallback} func a callback called for each chunk.
     */
    each(func) {
        return this.tap().map(
            (a) => Promise.resolve(func(a))
                .then(() => a)
        ).resume();
    }

    /**
     * Reads the stream while the function outcome is truthy.
     *
     * Stops reading and emits end as soon as it ends.
     *
     * @param  {FilterCallback} func The condition check
     * @return {DataStream}  the shortened stream
     */
    while(func) {
        let condition = true;
        const out = this._selfInstance({
            parallelTransform: func,
            beforeTransform: (chunk) => condition ? chunk : Promise.reject(DataStream.filter),
            afterTransform: (chunk, ret) => {
                if (!ret) {
                    condition = false;
                    out.end();
                    return Promise.reject(DataStream.filter);
                } else {
                    return chunk;
                }
            },
            referrer: this
        });
        return this.pipe(out);
    }

    /**
     * Reads the stream until the function outcome is truthy.
     *
     * Works oposite of while.
     *
     * @param  {FilterCallback} func The condition check
     * @return {DataStream}  the shortened stream
     */
    until(func) {
        return this.while((...args) => Promise.resolve(func(...args)).then((a) => !a));
    }

    /**
     * Provides an way to catch errors in chanined streams.
     *
     * The handler will be called as asynchronous
     *  - if it resolves then the error will be muted.
     *  - if it rejects then the error will be passed to the next handler
     *
     * If no handlers will resolve the error, an `error` event will be emitted
     *
     * @param {Function} callback Error handler (async function)
     * @chainable
     */
    catch(callback) {
        this._error_handlers.push(callback);
        return this;
    }

    /**
     * Executes all error handlers and if none resolves, then emits an error.
     *
     * The returned promise will always be resolved even if there are no succesful handers.
     *
     * @param  {Error} err The thrown error
     * @return {Promise}  the promise that will be resolved when the error is handled.
     */
    raise(err) {
        return this._error_handlers
            .reduce((err, handler) => err.catch(handler), Promise.reject(err))
            .catch((err) => this.emit('error', err))
        ;
    }

    /**
     * Override of node.js Readable pipe.
     *
     * Except for calling overriden method it proxies errors to piped stream.
     *
     * @param  {Writable} to  Writable stream to write to
     * @param  {WritableOptions} options
     * @return {Writable}  the `to` stream
     */
    pipe(to, options) {
        if (to === this) {
            return this;
        }

        if (this !== to && to instanceof DataStream) {
            to.setOptions({referrer: this});
            this.tap()
                .on("error", (...err) => to.raise(...err));
        } else if (to instanceof Readable) {
            this.on("error", (...err) => to.emit("error", ...err));
        }

        return super.pipe(to, options || {end: true});
    }

    /**
     * Creates a BufferStream
     *
     * @param  {MapCallback} serializer A method that converts chunks to buffers
     * @return {BufferStream}  the resulting stream
     *
     * @example {@link ../samples/data-stream-tobufferstream.js}
     */
    bufferify(serializer) {
        return this.map(serializer, scramjet.BufferStream);
    }

    /**
     * Creates a StringStream
     *
     * @param  {MapCallback} serializer A method that converts chunks to strings
     * @return {StringStream}  the resulting stream
     *
     * @example {@link ../samples/data-stream-tostringstream.js}
     */
    stringify(serializer) {
        return this.map(serializer, scramjet.StringStream);
    }

    /**
     * Consumes all stream items without doing anything
     *
     * @return {Promise} Resolved when the whole stream is read
     */
    async run() {
        return this.each(() => 0).whenEnd();
    }

    /**
     * Alias for {@link DataStream#stringify}
     * @function toStringStream
     */

    /**
     * Create a DataStream from an Array
     *
     * @param  {Array} arr list of chunks
     * @return {DataStream}  the resulting stream
     *
     * @example {@link ../samples/data-stream-fromarray.js}
     */
    static fromArray(arr) {
        const ret = new DataStream();
        arr = arr.slice();
        process.nextTick(() => {
            arr.forEach((item) => ret.write(item));
            ret.end();
        });
        return ret;
    }


    /**
     * Create a DataStream from an Iterator
     *
     * Doesn't end the stream until it reaches end of the iterator.
     *
     * @param  {Iterator} iter the iterator object
     * @return {DataStream}  the resulting stream
     *
     * @example {@link ../samples/data-stream-fromiterator.js}
     */
    static fromIterator(iter) {
        return new DataStream({
            read() {
                const read = iter.next();
                if (read.done) {
                    this.push(null);
                } else {
                    Promise.resolve(read.value)
                        .then((value) => this.push(value));
                }
            }
        });
    }

    /**
     * Aggregates the stream into a single Array
     *
     * In fact it's just a shorthand for reducing the stream into an Array.
     *
     * @param  {Array} initial Optional array to begin with.
     * @return {Promise} Promise resolved with the resulting array on stream end.
     */
    toArray(initial) {
        return this.reduce(
            (arr, item) => (arr.push(item), arr),
            initial || []
        );
    }

    /**
     * Returns an async generator
     *
     * Ready for https://github.com/tc39/proposal-async-iteration
     *
     * @return {Iterable.<Promise.<*>>} Returns an iterator that returns a promise for each item.
     */
    toGenerator() {
        this.tap();
        const ref = this;
        return function*() {
            let ended = false;
            ref.on("end", () => ended = true);
            while (!ended) {
                yield ref.whenRead();
            }
            return;
        };
    }

    /**
     * Stops merging transform callbacks at the current place in the command chain.
     *
     * @name tap
     * @function
     * @example {@link ../samples/data-stream-tap.js}
     */

    /**
     * Reads a chunk from the stream and resolves the promise when read.
     *
     * @name whenRead
     * @function
     * @async
     * @return {Promise<Object>}  the read item
     */

    /**
     * Writes a chunk to the stream and returns a Promise resolved when more chunks can be written.
     *
     * @name whenWrote
     * @function
     * @async
     * @return {Promise}
     */

    /**
     * Resolves when stream ends - rejects on uncaught error
     *
     * @name whenEnd
     * @function
     * @async
     * @return {Promise}
     */

    /**
     * Returns a promise that resolves when the stream is drained
     *
     * @name whenDrained
     * @function
     * @async
     * @return {Promise}
     */

    /**
     * Returns a promise that resolves (!) when the stream is errors
     *
     * @name whenDrained
     * @function
     * @async
     * @return {Promise}
     */

    /**
     * Allows resetting stream options.
     *
     * It's much easier to use this in chain than constructing new stream:
     *
     * ```javascript
     *     stream.map(myMapper).filter(myFilter).setOptions({maxParallel: 2})
     * ```
     *
     * @meta.conds keep-order,chain
     *
     * @name setOptions
     * @function
     * @param {StreamOptions} options
     * @chainable
     */

    /**
     * Returns a new instance of self.
     *
     * Normally this doesn't have to be overridden.
     * When the constructor would use some special arguments this may be used to
     * override the object construction in {@link tee}...
     *
     * @internal
     * @return {DataStream}  an empty instance of the same class.
     * @example {@link ../samples/data-stream-selfinstance.js}
     */
    _selfInstance(...args) {
        return new this.constructor(...args);
    }

}

DataStream.prototype.toBufferStream = DataStream.prototype.bufferify;
DataStream.prototype.toStringStream = DataStream.prototype.stringify;

module.exports = DataStream;

/**
 * @module ScramjetCore
 */

const {plgctor} = require('./util/promise-transform-stream');

/**
 * Exports
 *
 * @name scramjet
 * @type {Object}
 */
module.exports = {
    fromArray(...args) {
        return this.DataStream.fromArray(...args);
    },

    /**
     * Definition of a single mixin for a specific Scramjet class
     *
     * @typedef {Object} StreamMixin
     * @property {Function} constructor optional constructor that will be called in the stream constructor (this has to be an own property!)
     * @property {Function} * any name given will be mixed in to the scramjet stream (except for constructor)
     */

    /**
     * Definition of a plugin in Scramjet
     *
     * @typedef {Object} ScramjetPlugin
     * @property {StreamMixin} BufferStream definition of constructor and properties for the BufferStream prototype.
     * @property {StreamMixin} DataStream definition of constructor and properties for the DataStream prototype.
     * @property {StreamMixin} MultiStream definition of constructor and properties for the MultiStream prototype.
     * @property {StreamMixin} StringStream definition of constructor and properties for the StringStream prototype.
     */

    /**
     * Add a global plugin to scramjet - injects mixins into prototypes.
     *
     * @param  {ScramjetPlugin} mixin the plugin object
     * @chainable
     *
     * @example {@link ../samples/scramjet-plugin.js}
     */
    plugin(mixins) {
        for (const key of Object.keys(mixins)) {
            if (key in this) {
                const Mixin = mixins[key];
                const Stream = this[key];
                if (Mixin.hasOwnProperty("constructor") && Stream[plgctor]) {
                    Stream[plgctor].ctors.push(Mixin.constructor);
                    delete Mixin.constructor;
                }
                Object.getOwnPropertyNames(Mixin).forEach(
                    (prop) => Object.defineProperty(Stream.prototype, prop, Object.getOwnPropertyDescriptor(Mixin, prop))   // jshint ignore:line
                );
            } else {
                this[key] = mixins[key];
            }
        }
        return this;
    },

    /**
     * Provides a lazy-load accessor to BufferStream
     * @see module:ScramjetCore~BufferStream#
     */
    get BufferStream() { return require("./buffer-stream"); },
    /**
     * Provides a lazy-load accessor to DataStream
     * @see module:ScramjetCore~DataStream#
     */
    get DataStream() { return require("./data-stream"); },
    /**
     * Provides a lazy-load accessor to MultiStream
     * @see module:ScramjetCore~MultiStream#
     */
    get MultiStream() { return require("./multi-stream"); },
    /**
     * Provides a lazy-load accessor to StringStream
     * @see module:ScramjetCore~StringStream#
     */
    get StringStream() { return require("./string-stream"); },
    /**
     * Provides a lazy-load accessor to PromiseTransformStream
     * @see module:ScramjetCore~PromiseTransformStream#
     */
    get PromiseTransformStream() { return require("./util/promise-transform-stream").PromiseTransformStream; },
    /**
     * Gets an API version (this may be important for future use)
     * @param {Number} version The required version (currently only: 1)
     */
    API(version) {
        if (version === 1) {
            return module.exports;
        }
    }
};

/**
 * @module ScramjetCore
 */

const OUT = Symbol("OUT");

const mergesortStream = require("./util/merge-sort-stream");
const EventEmitter = require("events").EventEmitter;
const scramjet = require("./");

/**
 * An object consisting of multiple streams than can be refined or muxed.
 */
class MultiStream extends EventEmitter {

    /**
     * Crates an instance of MultiStream with the specified stream list
     *
     * @param  {stream.Readable[]} streams the list of readable streams (other
     *                                     objects will be filtered out!)
     * @param  {Object} options Optional options for the super object. ;)
     *
     * @example {@link ../samples/multi-stream-constructor.js}
     */
    constructor(streams, ...args) {

        super(args.length ? args[0] : streams);

        /**
         * Array of all streams
         * @type {Array}
         */
        this.streams = [];

        if (Array.isArray(streams))
            streams.forEach(
                (str) => this.add(str)
            );
    }

    /**
     * Returns the current stream length
     * @return {number}
     */
    get length() {
        return this.streams.length;
    }

    /**
     * Returns new MultiStream with the streams returned by the tranform.
     *
     * Runs callback for every stream, returns a new MultiStream of mapped
     * streams and creates a new multistream consisting of streams returned
     * by the callback.
     * @todo For later add/remove operations to work properly, the stream must
     * currently return the same instance!
     *
     * @param  {MapCallback} aFunc Mapper ran in Promise::then (so you can
     *                                  return a promise or an object)
     * @return {MultiStream}  the mapped instance
     *
     * @example {@link ../samples/multi-stream-map.js}
     */
    map(aFunc, rFunc) {
        return Promise.all(
            this.streams.map(
                (s) => {
                    return Promise.resolve(s)
                        .then(aFunc)
                    ;
                }
            )
        ).then(
            (streams) => {
                const out = new MultiStream(
                    streams
                );

                this.on(
                    "add",
                    (stream) => Promise.resolve(stream)
                        .then(aFunc)
                        .then(out.add.bind(out))
                );

                if (rFunc)
                    this.on(
                        "remove",
                        (stream) => Promise.resolve(stream)
                            .then(rFunc)
                            .then(out.remove.bind(out))
                    );

                return out;
            }
        );
    }

    /**
     * Calls Array.prototype.find on the streams
     * @param  {Arguments} args arguments for
     * @return {DataStream}  found DataStream
     */
    find(...args) {
        return this.streams.find(...args);
    }

    each(aFunc, rFunc) {
        return Promise.all(
            this.streams.map(
                (s) => {
                    return Promise.resolve(s)
                        .then(aFunc)
                    ;
                }
            )
        ).then(
            () => {
                this.on(
                    "add",
                    (stream) => Promise.resolve(stream).then(aFunc)
                );

                if (rFunc)
                    this.on(
                        "remove",
                        (stream) => Promise.resolve(stream).then(rFunc)
                    );

                return this;
            }
        );
    }

    /**
     * Filters the stream list and returns a new MultiStream with only the
     * streams for which the callback returned true
     *
     * @param  {TransformFunction} func Filter ran in Promise::then (so you can
     *                                  return a promise or a boolean)
     * @return {MultiStream}  the filtered instance
     *
     * @example {@link ../samples/multi-stream-filter.js}
     */
    filter(func) {
        return Promise.all(
            this.streams.map(
                (s) => Promise.resolve(s)
                    .then(func)
                    .then((o) => o ? s : null)
            )
        ).then(
            (streams) => {
                const out = new MultiStream(
                    streams.filter((s) => s)
                );
                this.on(
                    "add",
                    (stream) => Promise.resolve(stream)
                        .then(func)
                        .then(out.add.bind(out))
                );
                this.on(
                    "remove", out.remove.bind(out)
                );
                return out;
            }
        );
    }

    /**
     * Muxes the streams into a single one
     *
     * @todo For now using comparator will not affect the mergesort.
     * @todo Sorting requires all the streams to be constantly flowing, any
     *       single one drain results in draining the muxed too even if there
     *       were possible data on other streams.
     *
     * @param  {ComparatorFunction} cmp Should return -1 0 or 1 depending on the
     *                                  desired order. If passed the chunks will
     *                                  be added in a sorted order.
     * @return {DataStream}  The resulting DataStream
     *
     * @example {@link ../samples/multi-stream-mux.js}
     */
    mux(cmp, Clazz) {

        this[OUT] = Clazz ? new Clazz() : new scramjet.DataStream();

        if (!cmp) {

            const unpipeStream = (stream) => {
                if (stream) stream.unpipe(this[OUT]);
                this[OUT].setMaxListeners(this.streams.length);
            };

            const pipeStream = (stream) => {
                this[OUT].setMaxListeners(this.streams.length);
                stream.pipe(this[OUT], {end: false});
            };

            this.on("add", pipeStream);
            this.on("remove", unpipeStream);

            this.streams.forEach(pipeStream);

            this.on("empty", () => this[OUT].end());

            return this[OUT];
        }

        return mergesortStream(this, cmp, 0, Clazz);
    }

    /**
     * Adds a stream to the MultiStream
     *
     * If the stream was muxed, filtered or mapped, this stream will undergo the
     * same transorms and conditions as if it was added in constructor.
     *
     * @param {stream.Readable} stream [description]
     *
     * @example {@link ../samples/multi-stream-add.js}
     */
    add(stream) {

        if (stream) {
            this.streams.push(stream);
            this.setMaxListeners(this.streams.length + EventEmitter.defaultMaxListeners);
            this.emit("add", stream, this.streams.length - 1);
            stream.on("end", () => this.remove(stream));
        }

        return this;
    }

    /**
     * Removes a stream from the MultiStream
     *
     * If the stream was muxed, filtered or mapped, it will be removed from same
     * streams.
     *
     * @param {stream.Readable} stream [description]
     *
     * @example {@link ../samples/multi-stream-remove.js}
     */
    remove(stream) {

        const strIndex = this.streams.indexOf(stream);
        if (strIndex >= 0) {
            this.setMaxListeners(this.streams.length + EventEmitter.defaultMaxListeners);
            this.streams.splice(strIndex, 1);
            this.emit("remove", stream, strIndex);
        }

        if (!this.streams.length) {
            this.emit("empty");
        }

        return this;
    }

}

module.exports = MultiStream;

